clc;clear all;close all %clear the command lineclear; %remove all previous variablesPlotXmin=-6;%valor x minimo en el espacio de la parcelaPlotXmax=6;%%valor x maximo en el espacio de la parcelaPlotYmin=-6;%valor x minimo en el espacio de la parcelaPlotYmax=6;%valor y maximo en el espacio de la parcelaPlotZmin=-6;%lvalor z minimo en el espacio de la parcelaPlotZmax=6;%valor z maximo en el espacio de la parcelaNumberOfXPlottingPoints=5;%número de puntos de trazado a lo largo del eje xNumberOfYPlottingPoints=5;%número de puntos de trazado a lo largo del eje yNumberOfZPlottingPoints=5;%número de puntos de trazado a lo largo del eje zPlotStepX=(PlotXmax-PlotXmin)/(NumberOfXPlottingPoints-1);%trazar el paso en la dirección xPlotStepY=(PlotYmax-PlotYmin)/(NumberOfYPlottingPoints-1);%trazar el paso en la dirección yPlotStepZ=(PlotZmax-PlotZmin)/(NumberOfZPlottingPoints-1);%trazar el paso en la dirección z[X,Y,Z]=meshgrid(PlotXmin:PlotStepX:PlotXmax,PlotYmin:PlotStepY:PlotYmax,PlotZmin:PlotStepZ:PlotZmax);%construir matrices de espacio de parcelaFx=zeros(NumberOfZPlottingPoints,NumberOfYPlottingPoints,NumberOfXPlottingPoints);%x componente de la densidad de flujoFy=zeros(NumberOfZPlottingPoints,NumberOfYPlottingPoints,NumberOfXPlottingPoints);%y componente de la densidad de flujoFz=zeros(NumberOfZPlottingPoints,NumberOfYPlottingPoints,NumberOfXPlottingPoints);%z componente de la densidad de flujoQ=-250e-6;%carga puntualC=[7 -2 4];%localizacion de la carga puntualF=[0 0 0];%inicializar la densidad de flujofor k=1:NumberOfZPlottingPointsfor j=1:NumberOfYPlottingPointsfor i=1:NumberOfXPlottingPointsXplot=X(k,j,i);%coordenada x del punto de la trama actualYplot=Y(k,j,i);%coordenada y del punto de la trama actualZplot=Z(k,j,i);%coordenada z del punto de la trama actualP=[Xplot Yplot Zplot];%vector de posición de los puntos de observaciónR=P-C; %vector que apunta desde la carga puntual al punto de observación actualRmag=norm(R);%magnitud de Rif (Rmag>0)% sin línea de flujo definida en la fuenteR_Hat=R/Rmag;%vector unitario de RF=Q*R_Hat/(4*pi*Rmag^2);%densidad de flujo del punto de observación actualFx(k,j,i)=F(1,1);%obtener el componente x en el punto de observación actualFy(k,j,i)=F(1,2);%obtener el componente y en el punto de observación actualFz(k,j,i)=F(1,3);%%obtener el componente z en el punto de observación actualendendendendquiver3(X,Y,Z,Fx,Fy,Fz)title('Simulacion Carga en el origen Q2')